// MealDeals.js
import React, { useState } from 'react';
import styles from './MealDeals.module.css';  // Ensure the path is correct based on your project structure

const MealDeal = () => {
  const [selectedMealDeal, setSelectedMealDeal] = useState(null);
  const [details, setDetails] = useState({
    'PopcornExtravaganza': 0,
    'Pepperoni': 0,
    'Sausage': 0,
    'Cheese': 0,
    'HotDogsWithFries': 0,
    'ChickenStripsWithFries': 0
  });

  const mealDeals = ['PopcornExtravaganza', 'ReelPizzaDeal', 'RealMealDeal'];

  const handleSelect = (deal) => {
    if (selectedMealDeal === deal) {
      setSelectedMealDeal(null);
    } else {
      setSelectedMealDeal(deal);
    }
  };

  const handleChange = (field, value) => {
    setDetails(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div>
      <h2>Select Your Meal Deal</h2>
      <div>
        {mealDeals.map((deal) => (
          <button
            key={deal}
            onClick={() => handleSelect(deal)}
            className={selectedMealDeal === deal ? styles.buttonSelected : styles.button}
          >
            {deal}
          </button>
        ))}
      </div>
      {selectedMealDeal && (
        <div className={styles.details}>
          <h3>Details for {selectedMealDeal}</h3>
          {selectedMealDeal === 'PopcornExtravaganza' && (
            <div>
              <label>
                How many buckets of popcorn?
                <input type="number" value={details['PopcornExtravaganza']} onChange={(e) => handleChange('PopcornExtravaganza', e.target.value)} />
              </label>
            </div>
          )}
          {selectedMealDeal === 'ReelPizzaDeal' && (
            <div>
              <label>
                Pepperoni Pizzas:
                <input type="number" value={details['Pepperoni']} onChange={(e) => handleChange('Pepperoni', e.target.value)} />
              </label>
              <label>
                Sausage Pizzas:
                <input type="number" value={details['Sausage']} onChange={(e) => handleChange('Sausage', e.target.value)} />
              </label>
              <label>
                Cheese Pizzas:
                <input type="number" value={details['Cheese']} onChange={(e) => handleChange('Cheese', e.target.value)} />
              </label>
            </div>
          )}
          {selectedMealDeal === 'RealMealDeal' && (
            <div>
              <label>
                Hot Dogs with Fries:
                <input type="number" value={details['HotDogsWithFries']} onChange={(e) => handleChange('HotDogsWithFries', e.target.value)} />
              </label>
              <label>
                Chicken Strips with Fries:
                <input type="number" value={details['ChickenStripsWithFries']} onChange={(e) => handleChange('ChickenStripsWithFries', e.target.value)} />
              </label>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MealDeal;
