import React from 'react';

const Payment = () => {
  // Tip adding buttons
  const onNoTipPress = () => {
    alert("You chose not to tip.");
  }
  const _5percentPress = () => {
    alert("Thank you! Your tip will be added to your total.");
  }
  const _10percentPress = () => {
    alert("Thank you! Your tip will be added to your total.");
  }
  const _20percentPress = () => {
    alert("Thank you! Your tip will be added to your total.");
  }
  const customTip = () => {
    alert("How much would you like to tip?");
  }

  return (
    <div style={{ backgroundColor: 'white' }}>
      <h1 style={{ fontSize: '25px', margin: '20px', padding: '15px', textAlign: 'center' }}>St. Michael Cinema</h1>
      <div style={{ overflowY: 'scroll' }}>
        <p style={{ fontSize: '20px', padding: '20px', margin: '10px' }}>
          PAYMENT {'\n'}
          'room selection' {'\n'}
          'meal deal selection with any add-ons selected'
        </p>
        <div style={styles.Setup}>
          <button onClick={onNoTipPress} style={styles.Buttons}>No TIP</button>
          <button onClick={_5percentPress} style={styles.Buttons}>5%</button>
          <button onClick={_10percentPress} style={styles.Buttons}>10%</button>
          <button onClick={_20percentPress} style={styles.Buttons}>20%</button>
          <button onClick={customTip} style={styles.Buttons}>Custom TIP</button>
        </div>
        <p style={{ margin: '10px', fontSize: '20px' }}>Payment due today</p>
        <p style={{ margin: '10px', fontSize: '10px' }}>You have a remaining balance of ___ due by your booking date. You will receive an email when your payment is due.</p>

        <input style={styles.Input} placeholder="Card number" />
        <input style={styles.Input} placeholder="MM/YY" />
        <input style={styles.Input} placeholder="CVV" />
        <button style={styles.Buttons}>
          <span style={styles.ButtonText}>Process my Payment and Book my Event</span>
        </button>
      </div>
    </div>
  );
};

export default Payment;

// Styles adapted for the web
const styles = {
  Setup: {
    backgroundColor: 'white',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  Buttons: {
    backgroundColor: 'orange',
    margin: '10px',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
  },
  ButtonText: {
    textAlign: 'center',
  },
  Input: {
    padding: '5px',
    margin: '20px',
    borderWidth: '1px',
    borderStyle: 'solid',
    borderColor: 'grey',
    display: 'block', // Ensure input elements are block level for full width
    width: 'calc(100% - 42px)', // Adjust based on padding and margin
  },
};
