import React, { useState } from 'react';

const DrinkDeal = () => {
  const [selectedDrinks, setSelectedDrinks] = useState([]);

  const drinks = ['Pepsi', 'Diet Pepsi', 'Root Beer', 'Starry', 'Lemonade', 'Dr. Pepper', 'Orange Crush', 'Water'];

  const handleSelectDrink = (drink) => {
    // Check if the drink is already selected
    if (selectedDrinks.includes(drink)) {
      // Remove the drink if it is already selected
      setSelectedDrinks(selectedDrinks.filter(d => d !== drink));
    } else {
      // Add the drink if less than two are selected
      if (selectedDrinks.length < 2) {
        setSelectedDrinks([...selectedDrinks, drink]);
      } else {
        // Optionally notify the user they can only select two drinks
        alert("You can only select up to two drinks.");
      }
    }
  };

  return (
    <div>
      <h2>Select Your Drinks</h2>
      {drinks.map((drink) => (
        <label key={drink}>
          <input
            type="checkbox"
            checked={selectedDrinks.includes(drink)}
            onChange={() => handleSelectDrink(drink)}
          />
          {drink}
        </label>
      ))}
    </div>
  );
};

export default DrinkDeal;
