import React, { Component, useRef, useState, useEffect } from 'react';
import {render} from 'react-dom';
import SignatureCanvas from 'react-signature-canvas';
import { useNavigate } from "react-router-dom";
import "./Contract.css"


const Contract = ()  =>  {
  let navigate = useNavigate();

    const sigRef = useRef();
  const [signature, setSignature] = useState(null);
  const handleSignatureEnd = () => {
    setSignature(sigRef.current.toDataURL());
  }
  const clearSignature = () => {
    sigRef.current.clear();
    setSignature(null);
  }

  useEffect(() => {
    console.log(signature);
  }, [signature]);
  return(
    <div>
      <h1>Building Policies</h1>
    <p>No outside food, beverage, or candy can be brought into the building. If you plan to bring in treat bags with food or candy for your guests, you must keep them until the conclusion of the event and hand them out as the guests are leaving the building.
Cake/cupcakes are allowed with the reservation of a party space; however, cake/cupcakes are not permitted to enter any auditorium. No balloons are permitted in any auditorium. A $250 cleaning fee will be added to your party total if this policy is not respected. Please keep in mind that there is no access to a freezer. Do not bring in an ice cream cake.
If you plan to bring in decorations, the only approved adhesive is blue painters tape. You will not be able to use any other type of tape. We do not allow any glitter or confetti.</p>
    <Security>

    </Security>
    <label>By signing below you acknowledge and accept the terms and regulations of the St. Michael Cinema</label>
    <SignatureCanvas 
      penColor="black"
      ref={sigRef}
      onEnd={handleSignatureEnd}
    />
    <button onClick={clearSignature}>Clear</button>
    <button type="button" onClick={() => navigate('/Summary')}>Back</button>
      <button type="button" onClick={() => navigate('/Payment')}>Continue</button>
  </div>
  );
}



export default Contract;
