import React from 'react';

// Assuming React Router and a Drawer component (from Material-UI, for example) are set up separately
import { useNavigate } from 'react-router-dom';

function UpcomingEvents() {
  return (
    <div style={{ padding: '30px', backgroundColor: 'white' }}>
      <button style={{ textAlign: 'right', margin: '20px', fontSize: '20px', backgroundColor: 'transparent', border: 'none' }}>Add A Host</button>
      <div style={styles.Setup}>
        <p> Date </p>
        <p> Party name and type </p>
      </div>
    </div>
  );
}

function PastEvents() {
  return (
    <div style={styles.Setup}>
    </div>
  );
}

function CreateEvents() {
  let navigate = useNavigate(); // Using React Router's hook for navigation

  return (
    <div style={styles.Setup}>
      <h1 style={styles.TitleText}>What kind of booking do you require?</h1>
      <div style={styles.SelectionView}>
        <h2 style={styles.Header}>Auditorium Rental</h2>
        <button onClick={() => navigate('/AuditoriumQuestions')} style={styles.Buttons}><span style={styles.ButtonText}>Book Now</span></button>

        <h2 style={styles.Header}>Third Floor Event</h2>
        <button onClick={() => navigate('/ThirdFloorQuestions')} style={styles.Buttons}><span style={styles.ButtonText}>Book Now</span></button>

        <h2 style={styles.Header}>Field Trip</h2>
        <button onClick={() => navigate('/FieldTripQuestions')} style={styles.Buttons}><span style={styles.ButtonText}>Book Now</span></button>
      </div>
    </div>
  );
}

// Drawer and navigation setup omitted for brevity

// Styles converted to CSS-in-JS
const styles = {
  Setup: {
    backgroundColor: 'white',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
  },
  Buttons: {
    backgroundColor: 'orange',
    margin: '10px',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
  },
  ButtonText: {
    textAlign: 'center',
  },
  SelectionView: {
    borderWidth: '4px',
    borderColor: 'black',
    padding: '5px',
    paddingBottom: '10px',
    margin: '10px',
  },
  HeaderText: {
    fontWeight: 'bold',
    fontSize: '25px',
  },
  TitleText: {
    fontSize: '40px',
    paddingBottom: '50px',
    color: 'grey',
    textAlign: 'center',
  },
};

// export { UpcomingEvents, PastEvents, CreateEvents }; // Export components for routing
